3. Format 함수의 사용 예

             using System;
             ...
             class Program
             {
                 static void Main(string[] args)
                 {
                     // Media 객체 생성
                     Media media = new Media();

                     // Format 옵션을 설정
                     media.SearchWriter();
                     IDiscRecorder2 _discRecorder = media.RecorderList[0];
                     bool _bEject = false;
                     bool _bQuick = false;

                     // Format 진행
                     media.Format(_discRecorder, _bEject, _bQuick);
                     Console.WriteLine("Format 성공.");
                 }
             }


